mapper.select:
SELECT MAX(screen_no)
FROM m_screen
WHERE movie_no=#{movie_no} AND location_no=#{location_no}

SELECT count(*) AS cnt
FROM m_screen
WHERE movie_no=#{movie_no} AND location_no=#{location_no}

SELECT count(*) AS count
FROM m_screen
WHERE location_no = #{location_no} AND screen_name = #{screen_name}

SELECT screen_no
FROM m_screen
WHERE movie_no = #{movie_no}
